import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './FeaturedArticles.css';

const FeaturedArticles = () => {
  const [latestTop, setLatestTop] = useState([]);
  const [latestBottom, setLatestBottom] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000')
      .then((response) => response.json())
      .then((data) => {
        setLatestTop(data.latest_top);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="featured-articles">
      <div className="container">

        <div className="row">
          {latestTop.map((article) => (
            <div key={article.id} className="col-md-6 mb-4">
              <div className="card h-100">
                <img src={article.banner_path} alt={article.title} className="card-img-top article-image" />
                <div className="card-body">
                  <h3 className="card-title"><a className='text' href={`/articles/${article.id}`}>{article.title}</a></h3>
                  <p className="card-text">{article.short_description}</p>

                </div>
              </div>
            </div>
          ))}
        </div>


      </div>
    </section>
  );
};

export default FeaturedArticles;
