import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';

function Header() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const getCsrfToken = () => Cookies.get('csrftoken');

    const checkAuthStatus = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/apis/login_status/', {
                method: 'GET',
                credentials: 'include',
                headers: {
                    'X-CSRFToken': getCsrfToken(),
                },
            });
            if (response.ok) {
                setIsLoggedIn(true);
            } else {
                setIsLoggedIn(false);
            }
        } catch (error) {
            console.error('Error checking authentication:', error);
            setIsLoggedIn(false);
        }
    };

    useEffect(() => {
        checkAuthStatus();
    }, []);

    const handleLogout = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/apis/logout/', {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'X-CSRFToken': getCsrfToken(),
                },
            });
            if (response.ok) {
                setIsLoggedIn(false);
                window.location.href = '/';
            } else {
                console.error('Failed to logout');
            }
        } catch (error) {
            console.error('Error logging out:', error);
        }
    };

    return (
        <header>
            <div>
                {isLoggedIn ? (
                    <>
                        <button onClick={handleLogout}>Logout</button>
                        <a href="/profile-page">Profile</a>
                        <a href="/new-post">New Post</a>
                        <a href="/all-posts">All Posts</a>
                    </>
                ) : (
                    <a href="/login/">Sign In</a>
                )}
            </div>
        </header>
    );
}

export default Header;
