import React, { useEffect, useState } from 'react';
import './header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isFixed, setIsFixed] = useState(false);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/login/', {
          method: 'GET',
          credentials: 'include', // Ensure credentials are sent (for Django's session)
        });
        if (response.ok) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
      } catch (error) {
        console.error('Error checking authentication:', error);
        setIsLoggedIn(false); // Set login status to false on error
      }
    };

    checkAuthStatus(); // Call the function when the component mounts
  }, []);

  const handleLogout = async () => {
    try {
      const response = await fetch('/logout/', {
        method: 'POST',
        credentials: 'include', // Ensure credentials are sent (for Django's session)
      });
      if (response.ok) {
        setIsLoggedIn(false);
        window.location.href = '/'; // Redirect to homepage after logout
      } else {
        console.error('Failed to logout');
      }
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector('.header');
      const headerHeight = header.offsetHeight;
      const scrollY = window.scrollY;
      setIsFixed(scrollY > headerHeight); // Fix the header when scrolled past its height
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <header className={`header ${isFixed ? 'header--fixed' : ''}`}>
      <div className="header__logo">
      <div className='navheader'><svg class="hamburger-icon top-header--menu-icon-open" alt="Navigation Menu" aria-labelledby="title" viewBox="0 0 24 24">
	<title>Navigation Menu</title>

		<g>
			<path d="M24,3.5H0V4.6H3.37V21H20.62V4.6H24ZM19.53,19.9H4.47v-15H19.53Z"></path>
			<rect x="7.5" y="8.07" width="9" height="1.1"></rect>
			<rect x="7.5" y="15.57" width="9" height="1.1"></rect>
			<rect x="7.5" y="11.82" width="9" height="1.1"></rect>
		</g>
	</svg></div>
        <a href="/">
          <img src="https://hbr.org/resources/css/images/HBR_logo_black.svg" alt="Harvard Business Review" />
        </a>
      </div>
      <div className="header__actions">
        <div className="sub">
          <button className="header__button">Subscribe</button>
          {isLoggedIn ? (
            <button className="header__button" onClick={handleLogout}>
              Logout
            </button>
          ) : (
            <a href="/login/" className="header__link">
              Sign In
            </a>
          )}
        </div>
        <button className="header__search">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="15" y2="15" />
          </svg>
        </button>
      </div>
    </header>
  );
}

export default Header;
