import React, { useEffect, useState } from 'react';
import './header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch('login', {
          method: 'GET',
          credentials: 'include', // Ensure credentials are sent (for Django's session)
        });
        if (response.ok) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
      } catch (error) {
        console.error('Error checking authentication:', error);
        setIsLoggedIn(false); // Set login status to false on error
      }
    };

    checkAuthStatus(); // Call the function when the component mounts
  }, []);

  const handleLogout = async () => {
    try {
      const response = await fetch('/logout/', {
        method: 'POST',
        credentials: 'include', // Ensure credentials are sent (for Django's session)
      });
      if (response.ok) {
        setIsLoggedIn(false);
        window.location.href = '/'; // Redirect to homepage after logout
      } else {
        console.error('Failed to logout');
      }
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <header className="header">
      <div className="header__logo">
        <a href="/">
          <img src="https://thepublive.com/assets/images/main_logo.svg" alt="Harvard Business Review" />
        </a>
      </div>
      <div className="header__actions">
        <button className="header__button">Subscribe</button>
        {isLoggedIn ? (
          <button className="header__button" onClick={handleLogout}>
            Logout
          </button>
        ) : (
          <a href="/login/" className="header__link">
            Sign In
          </a>
        )}
        <button className="header__search">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="15" y2="15" />
          </svg>
        </button>
      </div>
    </header>
  );
}

export default Header;
