// PostDetails.jsx

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PostDetails = ({ fetchUrl }) => {
  const { postId } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true); // State to manage loading indicator

  v

  if (loading) {
    return <div>Loading...</div>; // Display loading indicator while fetching data
  }

  if (!post) {
    return <div>Error: Unable to fetch post details</div>; // Display error message if fetch fails
  }

  return (
    <div className="post-details">
      <h2>{post.title}</h2>
      <p>{post.short_description}</p>
      <div dangerouslySetInnerHTML={{ __html: post.content }}></div>
      {/* Assuming banner_path is a URL to an image */}
      <img src={post.banner_path} alt={post.title} />
      <p>Keywords: {post.meta_keywords}</p>
      <p>Date Created: {new Date(post.date_created).toLocaleDateString()}</p>
      <p>Date Updated: {new Date(post.date_updated).toLocaleDateString()}</p>
      {/* Add any other details you want to display */}
    </div>
  );
};

export default PostDetails;
