import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LatestGrid.css';

const LatestNews = () => {
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/apis/posts/')
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setLatestPosts(data.slice(0, 3)); // Fetch only the latest 8 posts
        } else {
          throw new Error("Invalid data format");
        }
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="LatestGrid">
      <div className="container">
        <div className="LatestGridRow">
          {latestPosts.map((article) => (
            <div key={article.id} className="col-md-3 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">
                    <a className='text' href={`/articles/${article.id}`}>{article.title}</a>
                  </h5>
                  <p className="card-text">Author: {article.author}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};
function truncateText(text, maxWords) {
    const words = text.split(' ');
    if (words.length > maxWords) {
      return words.slice(0, maxWords).join(' ') + '...';
    }
    return text;
  }
export default LatestNews;
