import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardBody, CardTitle, CardText } from 'reactstrap'; // Assuming you are using Reactstrap for Bootstrap components

const LatestNews = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/posts/')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  return (
    <div>
      <h1>Latest News</h1>
      <div className="row">
        {posts.map(post => (
          <div key={post.id} className="col-md-4 mb-4">
            <Card>
              {/* Add image if you have a banner_path or similar */}
              {/* <CardImg top width="100%" src={post.banner_path} alt={post.title} /> */}
              <CardBody>
                <CardTitle tag="h5">{post.title}</CardTitle>
                <CardText>{post.short_description}</CardText>
                {/* Additional fields can be added here */}
              </CardBody>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Z;
