import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import './LatestGrid.css';
import { fetchCategoryDetails, fetchUserDetails } from '../../Services/apiService';

const LatestGrid = () => {
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/apis/posts/');
        const data = await response.json();
        if (Array.isArray(data)) {
          const postsWithDetails = await Promise.all(data.slice(0, 3).map(async (post) => {
            const userData = await fetchUserDetails(post.user);

            const categoryResponses = await Promise.all(
              post.categories.map(categoryId => fetchCategoryDetails(categoryId))
            );

            const categories = categoryResponses.map(category => ({
              id: category.id,
              name: category.name
            }));

            return { ...post, authorName: userData.username, categories };
          }));
          setLatestPosts(postsWithDetails);
        } else {
          throw new Error("Invalid data format");
        }
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="forYou">
      <div className="container Glat">
        <h5 className='gridhead'>Latest</h5>
        <div className="forYourow">
          {latestPosts.map((article) => (
            <div className='gridbd' key={article.id}>
              <div className="col-md-12 mb-4">
                <div className="CardLatestGrid">
                  <div className="card-body">
                    <h5 className="card-title">
                      <h2 className='foryoutitle'>
                        <Link to={`/articles/${article.id}`} className='featuredtext'>{article.title}</Link>
                      </h2>
                    </h5>
                    <p className="forYoutext">{truncateText(article.short_description, 18)}</p>
                    <div className="latest-categories">
                      {article.categories.map(category => (
                        <Link key={category.id} to={`/category/${category.id}/posts`} className="category-link">
                          {category.name}
                        </Link>
                      ))}
                    </div>
                    <p className="foryou-author">{article.authorName}</p> 
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

function truncateText(text, maxWords) {
  const words = text.split(' ');
  if (words.length > maxWords) {
    return words.slice(0, maxWords).join(' ') + '...';
  }
  return text;
}

export default LatestGrid;
