import React from 'react';
import './header.css';

function Header() {
  const handleLogout = () => {
    // Implement logout functionality
    // Example: You might want to send an AJAX request to your Django server to logout the user
    fetch('/logout/', {
      method: 'POST',
      credentials: 'same-origin',  // Ensure credentials are sent with the request
    }).then(response => {
      if (response.ok) {
        window.location.href = '/';  // Redirect to homepage after successful logout
      }
    }).catch(error => {
      console.error('Error logging out:', error);
    });
  };

  return (
    <header className="header">
      <div className="header__logo">
        <a href="/">
          <img src="https://thepublive.com/assets/images/main_logo.svg" alt="Harvard Business Review" />
        </a>
      </div>
      <div className="header__actions">
        <button className="header__button">Subscribe</button>
        <a href="/login" className="header__link">
          Sign In
        </a>
        <button className="header__search">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"a
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="15" y2="15" />
          </svg>
        </button>
        {/* Example logout button */}
        <button className="header__button" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </header>
  );
}

export default Header;
