import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FeaturedPost = ({ postId }) => {
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://127.0.0.1:8000/post/4/`)
      .then(response => {
        setPost(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, [postId]); // Fetch data whenever postId changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading post: {error.message}</p>;
  if (!post) return <p>No post found.</p>;

  return (
    <section className="featured-post">
      <div key={post.id} className="article-card">
            <img src={post.banner_path} alt={post.title} className="article-image" />
            <div className="article-content">
              <h3>{article.title}</h3>
              <p>{article.short_description}</p>
              <a href={`/articles/${article.id}`}>Read More</a>
            </div>
          </div>
    </section>
  );
};

export default FeaturedPost;
