import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LatestNews.css';

const hardcodedData = [
  {
    id: 9,
    title: "Why People Resist Retirement",
    short_description: "Research suggests making the decision to retire means grappling with three psychological issues...",
    content: "<p>With President Joe Biden’s withdrawal from the 2024 presidential race...</p>",
    banner_path: "http://127.0.0.1:8000/media/news_banner/Jul24_23_674683798.jpg",
    status: "1",
    meta_keywords: "job",
    date_created: "2024-07-23T20:37:49.803241+08:00",
    date_updated: "2024-07-23T20:37:49.847746+08:00",
    user: 3,
    categories: [1],
    tags: [1, 2]
  }
];

const LatestNews = () => {
  const [latestPosts, setLatestPosts] = useState([]);

  useEffect(() => {
    // Simulating the fetching of data
    setLatestPosts(hardcodedData);
  }, []);

  return (
    <section className="latest-news">
      <div className="container">
        <div className="row">
          {latestPosts.map((article) => (
            <div key={article.id} className="col-md-3 mb-4 LTLN">
              <div className="Home-card">
                <img src={article.banner_path} alt={article.title} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">
                    <a className="text Latest-Homepage-t" href={`/articles/${article.id}`}>{article.title}</a>
                  </h5>
                  <p className="card-text">Author: {article.user}</p> {/* Simplified for hardcoded data */}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LatestNews;
