import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './latest.css';

const LatestNews = () => {
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/apis/posts/')
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setLatestPosts(data.slice(0, 8)); // Fetch only the latest 8 posts
        } else {
          throw new Error("Invalid data format");
        }
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <div>
    <section className="latest-news">
      <div className="container">

        <div className="row">
          {latestPosts.map((article) => (
            <div key={article.id} className="col-md-3 mb-4 LTLN">
              <div className="card">
                <img src={article.banner_path} alt={article.title} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">
                    <a className='text' href={`/articles/${article.id}`}>{article.title}</a>
                  </h5>
                  <p className="card-text">Author: {article.user}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
    </div>
  );
};

export default LatestNews;
