import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './latest.css';

const LatestNews = ({ fetchUrl }) => {
  const { categoryId } = useParams();
  const location = useLocation();
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theLatest, setTheLatest] = useState("");

  useEffect(() => {
    let url;
    let latest;

    if (location.pathname === "/latest") {
      url = `${fetchUrl}/posts/`;
      latest = "Latest";
    } else if (categoryId) {
      url = `${fetchUrl}${categoryId}/posts/`;
      latest = "Category";
    }

    setTheLatest(latest);

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        if (location.pathname === "/latest" && Array.isArray(data)) {
          setLatestPosts(data.slice(0, 8));
        } else if (data.posts && Array.isArray(data.posts)) {
          setLatestPosts(data.posts.slice(0, 8));
        } else {
          throw new Error("Invalid data format");
        }
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, [fetchUrl, categoryId, location.pathname]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="latest-news-page">
      <div className="container">
        <h1 className='thelatest'>{theLatest}</h1>
        <div className="row">
          {latestPosts.map((article) => (
            <div key={article.id} className="latestp">
              <div className="Latestc">
                <img src={article.banner_path} alt={article.title} className="card-img-top latestimg" />
                <div className="card-body">
                  <h5 className="card-title">
                    <a className='text' href={`/articles/${article.id}`}>{article.title}</a>
                  </h5>
                  <p className="latest-author">Author: {article.user}</p>
                  <p className="latestptext">{truncateText(article.short_description, 50)}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Function to truncate text to a specific number of words
function truncateText(text, maxWords) {
  const words = text.split(' ');
  if (words.length > maxWords) {
    return words.slice(0, maxWords).join(' ') + '...';
  }
  return text;
}

export default LatestNews;
