import React, { useEffect, useState } from 'react';
import './header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);  // State to track login status

  useEffect(() => {
    // Check if the user is logged in when the component mounts
    fetch('/check-auth/', {  // Adjust endpoint based on your Django setup
      method: 'GET',
      credentials: 'same-origin',  // Ensure credentials are sent with the request
    }).then(response => {
      if (response.ok) {
        setIsLoggedIn(true);  // User is logged in
      } else {
        setIsLoggedIn(false);  // User is not logged in
      }
    }).catch(error => {
      console.error('Error checking authentication:', error);
    });
  }, []);

  const handleLogout = () => {
    // Implement logout functionality
    fetch('/logout/', {
      method: 'POST',
      credentials: 'same-origin',  // Ensure credentials are sent with the request
    }).then(response => {
      if (response.ok) {
        setIsLoggedIn(false);  // Update login status after successful logout
        window.location.href = '/';  // Redirect to homepage after logout
      }
    }).catch(error => {
      console.error('Error logging out:', error);
    });
  };

  return (
    <header className="header">
      <div className="header__logo">
        <a href="/">
          <img src="https://thepublive.com/assets/images/main_logo.svg" alt="Harvard Business Review" />
        </a>
      </div>
      <div className="header__actions">
        <button className="header__button">Subscribe</button>
        {isLoggedIn ? (
          <>
            <button className="header__button" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <a href="/login" className="header__link">
              Sign In
            </a>
          </>
        )}
        <button className="header__search">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="15" y2="15" />
          </svg>
        </button>
      </div>
    </header>
  );
}

export default Header;
