import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './';

const ForYou = ({ postId }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/apis/posts/`)
      .then((response) => response.json())
      .then((data) => {
        setArticles(data.slice(0, 2)); // Render two posts
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, [postId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="featured-articles">
      <div className="container">
        {articles.length ? (
          <div className="row">
            {articles.map(article => (
              <div key={article.id} className="col-md-6 mb-4">
                <div className="card h-100">
                  <img src={article.banner_path} alt={article.title} className="card-img-top article-image" />
                  <div className="card-body">
                    <h1 className="card-title"><a className='text' href={`/articles/${article.id}`}>{article.title}</a></h1>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No articles found</p>
        )}
      </div>
    </section>
  );
};

export default ForYou;
