import React, { useEffect, useState } from 'react';
import OverlayMenu from '../../FixedComponent/nav/Navigation_2'; // Adjust the path as necessary
import './header.css';

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
      const cookie = cookies[i].trim();
      // Does this cookie string begin with the name we want?
      if (cookie.substring(0, name.length + 1) === (name + '=')) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isFixed, setIsFixed] = useState(false);
  const [isOverlayOpen, setIsOverlayOpen] = useState(false);

  const toggleMenu = () => {
    setIsOverlayOpen(!isOverlayOpen);
  };

  const checkAuthStatus = async () => {
    try {
      const csrftoken = getCookie('csrftoken');
      const response = await fetch('http://127.0.0.1:8000/apis/login', {
        method: 'GET',
        credentials: 'include',
        headers: {
          'X-CSRFToken': csrftoken,
        },
      });
      console.log('Checking authentication status:', response.status);
      if (response.ok) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    } catch (error) {
      console.error('Error checking authentication:', error);
      setIsLoggedIn(false);
    }
  };

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const handleLogout = async () => {
    try {
      const csrftoken = getCookie('csrftoken');
      const response = await fetch('http://127.0.0.1:8000/apis/logout/', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'X-CSRFToken': csrftoken,
        },
      });
      console.log('Logout response:', response.status);
      if (response.ok) {
        setIsLoggedIn(false);
        window.location.href = '/';
      } else {
        console.error('Failed to logout');
      }
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector('.header');
      const headerHeight = header.offsetHeight;
      const scrollY = window.scrollY;
      setIsFixed(scrollY > headerHeight);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <header className={`header ${isFixed ? 'header--fixed' : ''}`}>
      <div className="header__logo">
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          viewBox="0 0 24 24" 
          role="d" 
          className="header-nav" 
          aria-hidden="true"
          onClick={toggleMenu}
          style={{ cursor: 'pointer' }}
        >
          <path d="M24 3.5H0v1.1h3.37V21h17.25V4.6H24zm-4.47 16.4H4.47v-15h15.06z"></path>
          <path d="M7.5 8.07h9v1.1h-9zM7.5 15.57h9v1.1h-9zM7.5 11.82h9v1.1h-9z"></path>
        </svg>
        <div className="h"></div>
        <a href="/">
          <img src="https://hbr.org/resources/css/images/HBR_logo_black.svg" className="header-img" alt="Harvard Business Review" />
        </a>
      </div>
      <div className="header__actions">
        <div className="sub">
          <button className="header__button f">Subscribe</button>
          {isLoggedIn ? (
            <>
              <button className="header__button l" onClick={handleLogout}>Logout</button>
              <button className="header__button">Profile</button>
            </>
          ) : (
            <a href="http://localhost:3000/login"><button className="header__button l">Login</button></a>
          )}
        </div>
      </div>
      <OverlayMenu isOpen={isOverlayOpen} toggleMenu={toggleMenu} />
    </header>
  );
}

export default Header;
