// src/services/apiService.js
const API_URL = 'http://127.0.0.1:8000/apis';

const fetchPosts = async (limit) => {
  const response = await fetch(`${API_URL}/posts/`);
  const data = await response.json();
  if (!Array.isArray(data)) {
    throw new Error("Invalid data format");
  }
  return data.slice(0, limit);
};

const fetchAuthor = async (userId) => {
  const response = await fetch(`${API_URL}/users/${userId}/`);
  const data = await response.json();
  return data.username; // Assuming username is the field for author name
};

const fetchPostWithDetails = async (limit) => {
  const posts = await fetchPosts(limit);
  const authorRequests = posts.map(post => fetchAuthor(post.user));
  const authors = await Promise.all(authorRequests);

  return posts.map((post, index) => ({
    ...post,
    author: authors[index],
  }));
};

export { fetchPostWithDetails };
