import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './forYou.css';

const ForYou = () => {
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/apis/posts/')
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          // Update the state to store the latest 2 posts
          setLatestPosts(data.slice(0, 2));
        } else {
          throw new Error("Invalid data format");
        }
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="forYou">
      <div className="container">
        <div className="forYourow">
          {latestPosts.map((article) => (
            <div key={article.id} className="col-md-12 mb-4">
              <div className="CardFo">
                <img src={article.banner_path} alt={article.title} className="card-img-top" />

                <div className="card-body">
                  <h5 className="card-title">
                    <a className='text' href={`/articles/${article.id}`}>{article.title}</a>
                  </h5>

                  <p className="forYoutext">{truncateText(article.short_description, 9)}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Function to truncate text to a specific number of words
function truncateText(text, maxWords) {
  const words = text.split(' ');
  if (words.length > maxWords) {
    return words.slice(0, maxWords).join(' ') + '...';
  }
  return text;
}

export default ForYou;
