import React, { useEffect, useState } from 'react';
import './FeaturedArticles.css';

const FeaturedArticles = () => {
  const [Featur, setFeatured] = useState([]);
  const [latestBottom, setLatestBottom] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000')
      .then((response) => response.json())
      .then((data) => {
        setFeatured(data.latest_top);
        setLatestBottom(data.latest_bottom);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="featured-articles">
      <div className="container">
        <h2>Latest</h2>
        <h3>Top Articles</h3>
        {latestTop.map((article) => (
          <div key={article.id} className="article-card">
            <img src={article.banner_path} alt={article.title} className="article-image" />
            <div className="article-content">
              <h3>{article.title}</h3>
              <p>{article.short_description}</p>
              <a href={`/articles/${article.id}`}>Read More</a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturedArticles;
