import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './FeaturedArticles.css';

const FeaturedArticle = ({ postId }) => {
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/api/articles/4/`)
      .then((response) => response.json())
      .then((data) => {
        setArticle(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, [postId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading article: {error.message}</p>;

  return (
    <section className="featured-article">
      <div className="container">
        <h2 className="mb-4">Featured Article</h2>

        {article && (
          <div className="row">
            <div className="col-md-12 mb-4">
              <div className="card h-100">
                <img src={article.banner_path} alt={article.title} className="card-img-top article-image" />
                <div className="card-body">
                  <h3 className="card-title">{article.title}</h3>
                  <p className="card-text">{article.short_description}</p>
                  <a href={`/articles/${article.id}`} className="btn btn-primary">Read More</a>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default FeaturedArticle;
