import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './article.cs'

const PostDetails = () => {
  const { postId } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true); // State to manage loading indicator
  const [error, setError] = useState(null); // State to manage error

  useEffect(() => {
    const fetchPostDetails = async () => {
      const fetchUrl = `http://127.0.0.1:8000/apis/posts/${postId}/`;
      try {
        const response = await fetch(fetchUrl);
        if (!response.ok) {
          throw new Error(`Failed to fetch post details: ${response.statusText}`);
        }
        const data = await response.json();
        setPost(data);
      } catch (error) {
        console.error('Error fetching post details:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPostDetails();
  }, [postId]);

  if (loading) {
    return <div>Loading...</div>; // Display loading indicator while fetching data
  }

  if (error) {
    return <div>Error: {error}</div>; // Display error message if fetch fails
  }

  if (!post) {
    return <div>No post details available.</div>; // Display message if post details are not available
  }

  return (
    <div className="post-details">
      <h1>{post.title}</h1>
      <img src={post.banner_path} className='article-img' alt={post.title} />
      <p>{post.short_description}</p>
      <div dangerouslySetInnerHTML={{ __html: post.content }}></div>

      <p>Keywords: {post.meta_keywords}</p>
      <p>Date Created: {new Date(post.date_created).toLocaleDateString()}</p>
      <p>Date Updated: {new Date(post.date_updated).toLocaleDateString()}</p>
      {/* Add any other details you want to display */}
    </div>
  );
};

export default PostDetails;
