import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LatestNews.css';

const LatestNews = () => {
  const [latestPosts, setLatestPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000')
      .then((response) => response.json())
      .then((data) => {
        setLatestPosts(data.slice(0, 8)); // Fetch only the latest 8 posts
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading articles: {error.message}</p>;

  return (
    <section className="latest-news">
      <div className="container">
        <h2 className="mb-4">Latest News</h2>
        <div className="row">
          {latestPosts.map((article) => (
            <div key={article.id} className="col-md-3 mb-4">
              <div className="card h-100">
                <img src={article.banner_path} alt={article.title} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text">{article.short_description}</p>
                  <a href={`/articles/${article.id}`} className="btn btn-primary">Read More</a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LatestNews;
